var ApiProxy 	= require('./apiproxy');
var Auth 		= require('./auth');
var User 		= require('./user');
var Client 		= require('./client');

formide = function( config ) {
	this.config = config;
	this.config.apiUrl = config.apiUrl || 'https://api2.formide.com';
	this.config.authUrl = config.authUrl || 'https://api2.formide.com/auth';
	return {
		config: this.config,
		apiproxy: new ApiProxy(this),
		auth: new Auth(this),
		user: new User(this),
		client: new Client(this)
	};
}

module.exports = formide;