var request = require('request');

function User(formide) {
	this.formide = formide;
}

User.prototype.get = function(accessToken, next) {
	request.get({
		url: this.formide.config.apiUrl + '/auth/me',
		strictSSL: false,
		auth: {
			bearer: accessToken
  		}
	}, function(error, response, body) {
		if(error) return next(JSON.parse(error));
		return next(null, JSON.parse(body));
	}.bind(this));
};

module.exports = User;